import React from 'react';
import {getMergeSortAnimations} from '../sortingAlgorithms/sortingAlgorithms.js';
import './SortingVisualizer.css';

const ANIMATION_SPEED_MS = 1;
const NUMBER_OF_ARRAY_BARS = 200;
const PRIMARY_COLOR = 'turqoise';
const SECONDARY_COLOR = 'red';

export default class SortingVisualizer extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            array: [],
        };

    }

    componentDidMount() {
        this.resetArray();

    }

    resetArray() {
        const array = [];
        var numBars = document.getElementById("scroll").value;
        numBars = 4;
        for (let i = 0; i < numBars; i++) {
            array.push(Math.floor(Math.random() * (730 - 5 + 1) + 5));
        }
        this.setState({array});
    }

    resizeArray() {
        const array = [];
        var numBars = document.getElementById("scroll").value;
        numBars = 4;
        for (let i = 0; i < numBars; i++) {
            array.push(Math.floor(Math.random() * (730 - 5 + 1) + 5));
        }
        this.setState({array});  
    }

    mergeSort() {
        const animations = getMergeSortAnimations(this.state.array);

        for (let i = 2; i < animations.length; i+=3) {
          const arrayBars = document.getElementsByClassName('array-bar');
        //   const isColorChange = i % 3 !== 2;
        //   if (isColorChange) {
        //     const [barOneIdx, barTwoIdx] = animations[i];
        //     const barOneStyle = arrayBars[barOneIdx].style;
        //     const barTwoStyle = arrayBars[barTwoIdx].style;
        //     const color = i % 3 === 0 ? SECONDARY_COLOR : PRIMARY_COLOR;
        //     setTimeout(() => {
        //       barOneStyle.backgroundColor = color;
        //       barTwoStyle.backgroundColor = color;
        //     }, i * ANIMATION_SPEED_MS);
        //   } else {
            setTimeout(() => {
              const [barOneIdx, newHeight] = animations[i];
              const barOneStyle = arrayBars[barOneIdx].style;
              barOneStyle.height = `${newHeight}px`;
            }, i * ANIMATION_SPEED_MS);
          }
        }
    

    render() {
        const {array} = this.state;
        const width = (1400/array.length)-1;
        console.log(width);

        return (
            <div className="sort-container">
                <div id="test" className = "array-container">
                {array.map((value, idx) => (
                    <div 
                        className = "array-bar" 
                        key={idx}
                        // style = {{height: `${value}px`, marginLeft: `${(idx*width)+idx+1}px`, width: width}}>
                        style = {{height: `${value}px`, width: 5}}>
                    </div>
                ))}
                </div>
                <div className = "button-container">
                    <button onClick={() => this.resizeArray()}>Generate New Array</button>
                    <button onClick={() => this.mergeSort()}>Merge Sort</button>
                    <input id="scroll" type="range" min="10" max="200" onChange={() =>this.resizeArray()}/>
                </div>
            </div>
        );
    }
}
